def print_menu():
    pystyle.Write.Print(banner, pystyle.Colors.green_to_white, interval=0.01)
    pystyle.Write.Print("Меню рецептов русских блюд:\n", pystyle.Colors.green_to_white, interval=0.01)
    for number, (name, _) in recipes.items():
        pystyle.Write.Print(f"{number}. {name}\n", pystyle.Colors.green_to_white, interval=0.01)

def show_recipe(number):
    name, recipe = recipes.get(number, (None, None))
    if name:
        pystyle.Write.Print(f"\nРецепт: {name}\n", pystyle.Colors.green_to_white, interval=0.01)
        pystyle.Write.Print(f"{recipe}\n", pystyle.Colors.green_to_white, interval=0.01)
    else:
        pystyle.Write.Print("\nРецепт не найден.\n", pystyle.Colors.green_to_white, interval=0.01)

def main():
    while True:
        print_menu()
        choice = input("Введите номер блюда (или 'q' для выхода): ")
        if choice.lower() == 'q':
            break
        if choice.isdigit():
            show_recipe(int(choice))
        else:
            pystyle.Write.Print("Пожалуйста, введите правильный номер.\n", pystyle.Colors.red_to_white, interval=0.01)

if __name__ == "__main__":
    main()